import React, { useState } from 'react';
import './projects.css';

import Proofs from '../../images/proofs_line_mockup.png';
import Huddle from '../../images/huddleup_mockup.png';
import Pacman from '../../images/pacman_mockup.png';

function Projects() {
    const [turnProject2, setHidden2] = useState(true);
    const [turnProject3, setHidden3] = useState(true);

    function project1Click() {
        var getProject1 = document.getElementsByClassName("project1");
        var getProject2 = document.getElementsByClassName("project2");
        (getProject1)[0].removeEventListener('mouseenter', project1Enter);
        (getProject1)[0].classList.remove("removeProject1");
        (getProject2)[0].classList.remove("turnProject2");
        setHidden2(true);
        setHidden3(true);
    }

    function project1Enter() {
        var getProject1 = document.getElementsByClassName("project1");
        var getProject2 = document.getElementsByClassName("project2");
        var getProject3 = document.getElementsByClassName("project3");
        (getProject1)[0].classList.remove("removeProject1");
        (getProject2)[0].classList.remove("turnProject2");
        (getProject3)[0].classList.remove("turnProject3");
        setHidden2(true);
        setHidden3(true);
    }

    function project2Click() {
        var getProject1 = document.getElementsByClassName("project1");
        var getProject2 = document.getElementsByClassName("project2");
        (getProject2)[0].removeEventListener('mouseenter', project2Enter);
        if (turnProject2) {
            (getProject2)[0].classList.add("turnProject2");
            (getProject1)[0].classList.add("removeProject1")
            setHidden2(false);
            setHidden3(true);
        } else {
            (getProject2)[0].classList.remove("turnProject2");
            (getProject1)[0].classList.remove("removeProject1")
            setHidden2(true);
        }
    }

    function project2Enter() {
        var getProject1 = document.getElementsByClassName("project1");
        var getProject2 = document.getElementsByClassName("project2");
        var getProject3 = document.getElementsByClassName("project3");
        (getProject1)[0].classList.add("removeProject1");
        (getProject2)[0].classList.add("turnProject2");
        (getProject3)[0].classList.remove("turnProject3");
        setHidden2(true);
        setHidden3(true);
    }

    function project3Click() {
        var getProject1 = document.getElementsByClassName("project1");
        var getProject3 = document.getElementsByClassName("project3");
        (getProject3)[0].removeEventListener('mouseenter', project3Enter);
        if (turnProject3) {
            (getProject3)[0].classList.add("turnProject3");
            setHidden3(false);
        } else {
            (getProject1)[0].classList.remove("removeProject1");
            (getProject3)[0].classList.remove("turnProject3");
            setHidden3(true);
        }
    }

    function project3Enter() {
        var getProject1 = document.getElementsByClassName("project1");
        var getProject2 = document.getElementsByClassName("project2");
        var getProject3 = document.getElementsByClassName("project3");
        (getProject1)[0].classList.add("removeProject1");
        (getProject2)[0].classList.remove("turnProject2");
        (getProject3)[0].classList.add("turnProject3");
        setHidden2(true);
        setHidden3(true);
    }

    return (
        <div>
            <div className="projects">
                <div className="projects-left-side">
                    <div className="project1" onMouseEnter={project1Enter} onClick={project1Click}>
                        Proofs Line
                    </div>
                    <div className="project2" onMouseEnter={project2Enter} onClick={project2Click}>
                        huddleUp
                    </div>
                    <div className="project3" onMouseEnter={project3Enter} onClick={project3Click}>
                        pacman
                    </div>
                </div>
                <div className="projects-right-side">
                    <div className="projects-img-container">
                        <img className="proofs-img" src={Proofs}/>
                    </div>
                    <div className="project1-1"></div>
                </div>
            </div>
        </div>
    )
}

export default Projects;
