import React, { useState } from 'react';
import './projects.css';

import Proofs from '../../images/proofs_line_mockup.png';
import Huddle from '../../images/huddleup_mockup.png';
import Pacman from '../../images/pacman_mockup.png';

function Projects() {
    const [turn, setHidden] = useState(true);

    function project2Click() {
        var getProject1 = document.getElementsByClassName("project1");
        var getProject2 = document.getElementsByClassName("project2");
        (getProject2)[0].removeEventListener('mouseleave', project2Leave);
        (getProject2)[0].removeEventListener('mouseenter', project2Enter);
        if (turn) {
            (getProject2)[0].classList.add("turnProject2");
            (getProject1)[0].classList.add("removeProject1")
            setHidden(false);
        } else {
            (getProject2)[0].classList.remove("turnProject2");
            (getProject1)[0].classList.remove("removeProject1")
            setHidden(true);
        }
    }

    function project2Enter() {
        var getProject1 = document.getElementsByClassName("project1");
        var getProject2 = document.getElementsByClassName("project2");
        (getProject2)[0].classList.add("turnProject2");
        (getProject1)[0].classList.add("removeProject1")
        if (turn) {
            (getProject2)[0].addEventListener('mouseleave', project2Leave);
        }
    }

    function project2Leave() {
        var getProject1 = document.getElementsByClassName("project1");
        var getProject2 = document.getElementsByClassName("project2");
        (getProject2)[0].classList.remove("turnProject2");
        (getProject1)[0].classList.remove("removeProject1")
    }

    return (
        <div>
            <div className="projects">
                <div className="projects-left-side">
                    <div className="project1" onMouseEnter={project1Enter} onClick={project1Click}>
                        Proofs Line
                    </div>
                    <div className="project2" onMouseEnter={project2Enter} onClick={project2Click}>
                        huddleUp
                    </div>
                    <div className="project3">
                        pacman
                    </div>
                </div>
                <div className="projects-right-side">
                    <div className="projects-img-container">
                        <img className="proofs-img" src={Proofs}/>
                    </div>
                    <div className="project1-1"></div>
                </div>
            </div>
        </div>
    )
}

export default Projects;
